import React, { Fragment } from 'react';
import {BrowserRouter as Router, Route, Switch} from "react-router-dom";
import './App.css';
import Homepage from "./components/Homepage";
import Login from "./components/auth/Login";
import Register from "./components/auth/Register";
import PouchDB from "pouchdb";

const App = () => {
  const db = new PouchDB("mycompany");
  const remoteDB = new PouchDB("https://1c2cacab-ba99-4078-94fe-077691f93edd-bluemix.cloudant.com/mycompany");

  remoteDB.login(username, password).then(function () {
    localDb.sync(remoteDB, {live: true, retry: true, /* other sync options */});
  });

  return(
    <Router>
      <Fragment>
        <Route exact path="/" component={Homepage} />
        <section className="container">
          <Switch>
            <Route exact path="/register" component={Register} />
            <Route exact path="/login" component={Login} />
          </Switch>
        </section>
      </Fragment>
    </Router>
  );
}

export default App;
